mapper.select:
		select * from ((meeting m inner join meeting_room mr on m.mr_id = mr.mr_id)
		inner join user u on m.scheduler= u.user_id) inner join meeting_detail md on md.m_id = m.m_id 


		        and md.user_id=#{meetingDetail.user.userId}



					and mr.no like '%${meetingDetail.meeting.meetingRoom.no}%'


					and u.user_name like '%${meetingDetail.meeting.scheduler.userName}%'



					and m.m_name like '%${meetingDetail.meeting.mName}%'


                     and DATE_FORMAT(m.start_time, '%Y-%m-%d %H:%i:%s') >= DATE_FORMAT(#{meetingDetail.meeting.startTime}, '%Y-%m-%d %H:%i:%s') 


                     and DATE_FORMAT(m.end_time, '%Y-%m-%d %H:%i:%s') <= DATE_FORMAT(#{meetingDetail.meeting.endTime}, '%Y-%m-%d %H:%i:%s') 


                     and DATE_FORMAT(m.scheduled_time, '%Y-%m-%d %H:%i:%s') >= DATE_FORMAT(#{meetingDetail.meeting.scheduledTime}, '%Y-%m-%d %H:%i:%s') 


					and m.cancel = #{meetingDetail.meeting.cancel}




				and md.md_role = #{meetingDetail.role}


				and md.will = #{meetingDetail.will}


				and md.optional = #{meetingDetail.optional}


        order by m.start_time limit #{pageStart}, #{pageSize}

		select count(*) from ((meeting m inner join meeting_room mr on m.mr_id = mr.mr_id)
		inner join user u on m.scheduler= u.user_id) inner join meeting_detail md on md.m_id = m.m_id 


		        and md.user_id = #{user.userId}



					and mr.no like '%${meeting.meetingRoom.no}%'


					and u.user_name like '%${meeting.scheduler.userName}%'



					and m.m_name like '%${meeting.mName}%'


                     and DATE_FORMAT(m.start_time, '%Y-%m-%d %H:%i:%s') >= DATE_FORMAT(#{meeting.startTime}, '%Y-%m-%d %H:%i:%s') 


                     and DATE_FORMAT(m.end_time, '%Y-%m-%d %H:%i:%s') <= DATE_FORMAT(#{meeting.endTime}, '%Y-%m-%d %H:%i:%s') 


                     and DATE_FORMAT(m.scheduled_time, '%Y-%m-%d %H:%i:%s') >= DATE_FORMAT(#{meeting.scheduledTime}, '%Y-%m-%d %H:%i:%s') 


					and m.cancel = #{meeting.cancel}




				and md.md_role = #{role}


				and md.will = #{will}


				and md.optional = #{optional}

		select * from ((meeting m inner join meeting_room mr on m.mr_id = mr.mr_id)
		inner join user u on m.scheduler= u.user_id) inner join meeting_detail md on md.m_id = m.m_id 


		        and md.user_id = #{meetingDetail.user.userId}



					and mr.no like '%${meetingDetail.meeting.meetingRoom.no}%'


					and u.user_name like '%${meetingDetail.meeting.scheduler.userName}%'



					and m.m_name like '%${meetingDetail.meeting.mName}%'


                     and DATE_FORMAT(m.start_time, '%Y-%m-%d %H:%i:%s') >= DATE_FORMAT(#{meetingDetail.meeting.startTime}, '%Y-%m-%d %H:%i:%s') 


                     and DATE_FORMAT(m.end_time, '%Y-%m-%d %H:%i:%s') <= DATE_FORMAT(#{meetingDetail.meeting.endTime}, '%Y-%m-%d %H:%i:%s') 


                     and DATE_FORMAT(m.scheduled_time, '%Y-%m-%d %H:%i:%s') >= DATE_FORMAT(#{meetingDetail.meeting.scheduledTime}, '%Y-%m-%d %H:%i:%s') 


					and m.cancel = #{meetingDetail.meeting.cancel}




				and md.md_role = #{meetingDetail.role}


				and md.will = #{meetingDetail.will}


				and md.optional = #{meetingDetail.optional}



                 and DATE_FORMAT(m.start_time, '%Y-%m-%d %H:%i:%s') < DATE_FORMAT(#{nowTime}, '%Y-%m-%d %H:%i:%s') 


        order by m.start_time limit #{pageStart}, #{pageSize}

		select count(*) from ((meeting m inner join meeting_room mr on m.mr_id = mr.mr_id)
		inner join user u on m.scheduler= u.user_id) inner join meeting_detail md on md.m_id = m.m_id 


		        and md.user_id = #{meetingDetail.user.userId}



