mapper.select:
select
count(*) from user where user_name=#{userName} and
password=#{password}

	select * from user
	
		
			and user_id=#{userId}
        
		
			and user_name=#{userName}
        
		
			and password=#{password}

SELECT DISTINCT(u.user_id), u.user_name, u.sex, u.position FROM USER u INNER JOIN meeting_detail md ON u.user_id = md.user_id WHERE u.user_id IN
#{item}
AND EXISTS ( (SELECT * FROM meeting AS m WHERE DATE_FORMAT(m.start_time, '%Y-%m-%d %H:%i:%s') <= DATE_FORMAT(#{startTime}, '%Y-%m-%d %H:%i:%s') AND DATE_FORMAT(m.end_time, '%Y-%m-%d %H:%i:%s') > DATE_FORMAT(#{startTime}, '%Y-%m-%d %H:%i:%s') AND m.m_id = md.m_id) UNION (SELECT * FROM meeting mt WHERE DATE_FORMAT(mt.end_time, '%Y-%m-%d %H:%i:%s') >= DATE_FORMAT(#{endTime}, '%Y-%m-%d %H:%i:%s') AND DATE_FORMAT(mt.start_time, '%Y-%m-%d %H:%i:%s') < DATE_FORMAT(#{endTime}, '%Y-%m-%d %H:%i:%s') AND mt.m_id = md.m_id) UNION (SELECT * FROM meeting AS me WHERE DATE_FORMAT(me.start_time, '%Y-%m-%d %H:%i:%s') >= DATE_FORMAT(#{startTime}, '%Y-%m-%d %H:%i:%s') AND DATE_FORMAT(me.end_time, '%Y-%m-%d %H:%i:%s') <= DATE_FORMAT(#{endTime}, '%Y-%m-%d %H:%i:%s') AND me.m_id = md.m_id) )
